mapper.select:
SELECT 

FROM czz_aqsgbg a

WHERE a.id = #{id}

SELECT 

FROM czz_aqsgbg a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.sg_type = #{sgType}


		AND a.sb_time = #{sbTime}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM czz_aqsgbg a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE czz_aqsgbg SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
