mapper.select:
SELECT 

FROM ckwz_main a

WHERE a.id = #{id}

SELECT 

FROM ckwz_main a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.productname LIKE 
			'%'||#{productname}||'%'
			'%'+#{productname}+'%'
			concat('%',#{productname},'%')


		AND a.type = #{type}


		AND a.in_date BETWEEN #{beginInDate} AND #{endInDate}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM ckwz_main a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE ckwz_main SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
