mapper.select:
SELECT 

FROM ckwz_main a

WHERE a.id = #{id}

SELECT 

FROM ckwz_main a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.type LIKE 
			'%'||#{type}||'%'
			'%'+#{type}+'%'
			concat('%',#{type},'%')


		AND a.personname = #{personname}


		AND a.updatetime BETWEEN #{beginUpdatetime} AND #{endUpdatetime}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM ckwz_main a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE ckwz_main SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
