mapper.select:
SELECT 

FROM yjya_jbxx a

WHERE a.id = #{id}

SELECT 

FROM yjya_jbxx a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.comorganization = #{comorganization}


		AND a.username = #{username}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM yjya_jbxx a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE yjya_jbxx SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
