mapper.select:
SELECT 

FROM czz_yjyl a

WHERE a.id = #{id}

SELECT 

FROM czz_yjyl a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.date = #{date}


		AND a.location = #{location}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM czz_yjyl a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE czz_yjyl SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
