mapper.select:
SELECT 

FROM czz_zntx a

WHERE a.id = #{id}

SELECT 

FROM czz_zntx a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')


		AND a.person = #{person}


		AND a.start_date = #{startDate}


		AND a.end_date = #{endDate}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM czz_zntx a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE czz_zntx SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
